import React, { useRef, useState } from 'react';
import './Meter.css'; // Include your CSS file for styling

const Meter = () => {
  const [sliderValue, setSliderValue] = useState(6); // State to manage slider value
  const fileInputRef = useRef(null); // Create a ref for the file input

  // Handle slider value change
  const handleSliderChange = (e) => {
    const newValue = e.target.value;
    setSliderValue(newValue); // Update the slider value state
  };

  // Open file selection dialog
  const handleFileSelect = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click(); // Programmatically trigger the file input
    }
  };

  return (
    <>
      {/* File Upload Section */}
      <form className="fileUpload">
        <input type="file" className="fileElem" multiple ref={fileInputRef} // Reference the input
          style={{ display: 'none' }} // Hide the input element
        />
        <label className="fileSelect">Upload File onClick={handleFileSelect} // Trigger file selection on click</label>
        <h4>Usage</h4>
        <progress value="40" max="100"></progress>
        <p>
          4.00 GB out of 10 GB (40%) (
          <a href="#" className="upgrade">
            Upgrade
          </a>
          )
        </p>
      </form>

      {/* Slider Section */}
      <div className="sliderSection">
        <h4>Adjust Value</h4>
        <input
          type="range"
          min="0"
          max="10"
          value={sliderValue}
          onChange={handleSliderChange}
          className="slider"
        />
        <p>Current Value: {sliderValue}</p>
      </div>
    </>
  );
};

export default Meter;
