import React, { useRef, useState } from "react";
import "./Meter.css"; // Include your CSS file for styling

const Meter = () => {
  const [uploadedFiles, setUploadedFiles] = useState([]); // State to store uploaded files
  const fileInputRef = useRef(null); // Create a ref for the file input

  // Handle file selection
  const handleFilesUpload = (e) => {
    const files = Array.from(e.target.files); // Convert FileList to Array
    setUploadedFiles(files); // Save files in state
  };

  // Open file selection dialog
  const handleFileSelect = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click(); // Programmatically trigger the file input
    }
  };

  return (
    <>
      {/* File Upload Section */}
      <form className="fileUpload">
        <input
          type="file"
          className="fileElem"
          multiple
          ref={fileInputRef} // Reference the input
          style={{ display: "none" }} // Hide the input element
          onChange={handleFilesUpload} // Handle file upload
        />
        <label className="fileSelect" onClick={handleFileSelect}>
          Upload File
        </label>
        <h4>Uploaded Files</h4>
        <ul>
          {uploadedFiles.map((file, index) => (
            <li key={index}>
              {file.name} - {(file.size / (1024 * 1024)).toFixed(2)} MB
            </li>
          ))}
        </ul>
      </form>
    </>
  );
};

export default Meter;
