import React, { useState, useEffect } from 'react';
import './App.css';
import NavBar from './Components/NavBar';
import Inputs from './Components/Inputs';
import Meter from './Components/Meter';
import Table from './Components/Table';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [passCourses, setPassCourses] = useState([]);
  const [suggestedSubjects, setSuggestedSubjects] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/hello')
      .then(response => response.json())
      .then(data => setMessage(data.message));
  }, []);

  const handleFileUpload = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://127.0.0.1:5000/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Update state with passed courses and suggested subjects
      setPassCourses(response.data.pass_courses);
      setSuggestedSubjects(response.data["Subjects Suggested for Registration"]);
      setMessage(response.data.message); // Update message if needed
    } catch (error) {
      console.error("Error uploading file:", error);
      setMessage("Error uploading file.");
    }
  };

  return (
    <div className="App">
      <NavBar />
      <div className="Inputs">
        <Inputs onFileUpload={handleFileUpload} /> {/* Pass the upload handler to Inputs */}
      </div>
      <div className="Meter">
        <Meter />
        <p>{message}</p>
      </div>
      <div className="Table">
        <Table passCourses={passCourses} suggestedSubjects={suggestedSubjects} />
      </div>
    </div>
  );
}

export default App;




