import React, { useState } from 'react';
import './Meter.css'; // Include your CSS file for styling


  const [responseMessage, setResponseMessage] = useState(6);



    const Meter = () => {
      const [value, setValue] = useState(6); // State to manage slider value

      const handleInputChange = (e) => {
        const newValue = e.target.value;
        setValue(newValue); // Update the value state
      };

      // useEffect(() => {
      //   setValue(predictedLevel); // Update slider when predictedLevel changes
      // }, [predictedLevel]);

      return (
        <div className="range">
          <div className="sliderValue" style={{ left: `${((value - 3) / 4) * 100}%` }}>
            <span>{value}</span>
          </div>
          <div className="field">
            <div className="value left">Level 3</div>
            <input
              type="range"
              min="3"
              max="7"
              value={value}
              step="1"
              onChange={handleInputChange} // Handle value change
            />
            <div className="value right">Level 7</div>
          </div>
        </div>
      );
    };

    export default Meter; // Export Meter as default
