import React from 'react';
import './Table.css';

export default function Table({ passCourses, suggestedSubjects }) {
  // Remove duplicates by filtering suggestedSubjects
  const uniqueSuggested = suggestedSubjects.filter(
    subject => !passCourses.includes(subject)
  );

  const allSubjects = [...passCourses, ...uniqueSuggested];

  return (
    <div className="table-wrapper">
      <h3>Subject Summary</h3>
      <table className="styled-table">
        <thead>
          <tr>
            <th>Subject Code</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {allSubjects.map((subject, index) => {
            const isPass = passCourses.includes(subject);
            return (
              <tr key={index} className={isPass ? 'green-row' : 'black-row'}>
                <td>{subject}</td>
                <td>{isPass ? 'Passed' : 'Suggested'}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
