import React, { useRef, useState } from 'react';
import './Meter.css'; // Include your CSS file for styling

const Meter = () => {
  const [uploadedFiles, setUploadedFiles] = useState([]); // State to store uploaded files
  const [responseMessage, setResponseMessage] = useState(''); // State to handle server responses
  const fileInputRef = useRef(null); // Reference for file input

  // Handle file selection
  const handleFilesUpload = async (e) => {
    const files = Array.from(e.target.files); // Convert FileList to an Array
    setUploadedFiles(files); // Save files to the state

    // Prepare form data
    const formData = new FormData();
    files.forEach((file) => {
      formData.append('file', file); // Append each file to the FormData
    });

    // Send files to the backend
    try {
      const response = await fetch('http://127.0.0.1:5000/api/Cal_credit', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();

      // if (response.ok) {
      //   setResponseMessage(`Credits: ${result.credits}, Invalid Codes: ${result.invalid_codes.join(', ')}`);
      // } else if (result.error) {
      //   setResponseMessage(`Error: ${result.error}`);
      // } else {
      //   setResponseMessage(`Message: ${result.message}`);
      // }
    } catch (error) {
      setResponseMessage(`An error occurred: ${error.message}`);
    }
  };

  // Open file selection dialog
  const handleFileSelect = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click(); // Trigger the file input
    }
  };

  return (
    <div>
      <form className="fileUpload">
        {/* File input */}
        <input
          type="file"
          className="fileElem"
          multiple
          ref={fileInputRef} // Reference the input
          style={{ display: 'none' }} // Hide the input element
          onChange={handleFilesUpload} // Handle file selection
        />

        {/* Trigger file selection */}
        <label className="fileSelect" onClick={handleFileSelect}>
          Upload File
        </label>

        {/* Display server response */}
        {responseMessage && <p className="responseMessage">{responseMessage}</p>}
      </form>

      {/* Display uploaded file names */}
      {uploadedFiles.length > 0 && (
        <ul>
          {uploadedFiles.map((file, index) => (
            <li key={index}>
              {file.name} - {(file.size / (1024 * 1024)).toFixed(2)} MB
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Meter;
