import React, { useState } from 'react';
import './Meter.css'; // Include your CSS file for styling

const Meter = () => {
  const [sliderValue, setSliderValue] = useState(6); // State to manage slider value
  const [uploadedFiles, setUploadedFiles] = useState([]); // State to manage uploaded files

  const handleSliderChange = (e) => {
    const newValue = e.target.value;
    setSliderValue(newValue); // Update the slider value state
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files); // Convert FileList to an array
    setUploadedFiles(files); // Update the uploaded files state
  };

  return (
    <div className="range">
      {/* File Upload Form */}
      <form className="fileUpload">
        <input
          type="file"
          className="fileElem"
          multiple
          onChange={handleFileChange}
        />
        <label className="fileSelect">Upload File</label>
        <h4>Uploaded Files</h4>
        {uploadedFiles.length > 0 ? (
          <ul>
            {uploadedFiles.map((file, index) => (
              <li key={index}>{file.name}</li>
            ))}
          </ul>
        ) : (
          <p>No files uploaded yet</p>
        )}
        <h4>Usage</h4>
        <progress value={sliderValue * 10} max="100"></progress>
        <p>
          {sliderValue}.00 GB out of 10 GB ({sliderValue * 10}%) (
          <a href="#" className="upgrade">
            Upgrade
          </a>
          )
        </p>
      </form>

      {/* Slider */}
      <div className="sliderContainer">
        <label htmlFor="slider">Adjust Usage (GB): </label>
        <input
          id="slider"
          type="range"
          min="0"
          max="10"
          step="1"
          value={sliderValue}
          onChange={handleSliderChange}
        />
        <p>Current Value: {sliderValue} GB</p>
      </div>
    </div>
  );
};

export default Meter;
