import React, { useState } from 'react';
import './Meter.css'; // Include your CSS file for styling

const Meter = () => {
  const [value, setValue] = useState(6); // State to manage slider value

  const handleInputChange = (e) => {
    const newValue = e.target.value;
    setValue(newValue); // Update the value state
  };

  return (
    <div className="range">
      <div className="sliderValue" style={{ left: `${((value - 3) / 4) * 100}%` }}>
        <span>{value}</span>
      </div>
      <div className="field">
        <div className="value left">03</div>
        <input
          type="range"
          min="0"
          max="7"
          value={value}
          step="1"
          onChange={handleInputChange} // Handle value change
        />
        <div className="valu right">07</div>
      </div>
    </div>
  );
};

export default Meter; // Export Meter as default
